HAR(45) NOT NULL COMMENT '卡牌名称' ,`subtitle` VARCHAR(255) NOT NULL ,`popular_name` VARCHAR(255) NOT NULL ,`unittype` INT(11) NOT NULL ,`expansion_id` INT(11) NOT NULL ,`grade` INT(11) NOT NULL COMMENT '等级' ,`power` INT(11) NOT NULL COMMENT '能力' ,`shield` INT(11) NOT NULL COMMENT '盾护' ,`critical` INT(11) NOT NULL COMMENT '会心' ,`trigger` INT(11) NOT NULL ,`clan_id` INT(11) NOT NULL COMMENT '集团' ,`rarity_id` INT(11) NOT NULL ,`race_id` INT(11) NOT NULL COMMENT '种族' ,`skill` INT(11) NOT NULL COMMENT '技能描述' ,`effect` TEXT NOT NULL COMMENT '特技' ,`flavor_text` VARCHAR(255) NOT NULL ,`illustrator` VARCHAR(45) NOT NULL ,`remark` TEXT NOT NULL ,PRIMARY KEY (`id`) ,UNIQUE INDEX `card_no_UNIQUE` (`serial` ASC) )AUTO_INCREMENT=945;";				require_once(ABSPATH . 'wp-admin/includes/upgrade.php');		dbDelta($sql);		update_option( "vgcardutils_db_table_cards_version", $vgcardutils_db_table_cards_version );	}	//Add database table versions to options	add_option("vgcardutils_db_table_cards_version", $vgcardutils_db_table_cards_version);}//Create logs tablefunction vgcardutils_create_table_logs(){	//Get the table name with the WP database prefix	global $wpdb;	$table_name = $wpdb->prefix . "plugin_vgcardutils_logs";	global $vgcardutils_db_table_logs_version;	$installed_ver = get_option( "vgcardutils_db_table_logs_version" );	//Check if the table already exists and if the table is up to date, if not create it	if($wpdb->get_var("SHOW TABLES LIKE '$table_name'") != $table_name	||  $installed_ver != $vgcardutils_db_table_logs_version ) {		$sql = "CREATE TABLE " . $table_name . " (  `id` INT(11) NOT NULL AUTO_INCREMENT ,  `user_id` INT(11) NOT NULL ,  `card_id` INT(11) NOT NULL ,  `type` INT(11) NOT NULL ,  `data` TEXT NOT NULL ,  `time` DATETIME NOT NULL ,  PRIMARY KEY (`id`)             );";		require_once(ABSPATH . 'wp-admin/includes/upgrade.php');		dbDelta($sql);		update_option( "vgcardutils_db_table_logs_version", $vgcardutils_db_table_logs_