<?phpinclude plugin_dir_path(__FILE__).'includes/class-vgcards-list-table.php';function vg_card_list_page() {	if ( isset($_REQUEST['card']) ) {		vg_card_view_page($_REQUEST['card']);		return;	}	$table = new VgCards_List_Table();	$table->prepare_items();	?><div class="wrap">	<div id="icon-users" class="icon32">		<br />	</div>	<h2>所有卡片</h2>	<?php $table->description(); ?>	<form id="movies-filter" method="get">		<!-- For plugins, we also need to ensure that the form posts back to our current page -->		<input type="hidden" name="page"			value="<?php echo $_REQUEST['page'] ?>" />		<?php $table->display() ?>	</form></div><?php }?><?php function vg_card_view_page($id) {	global $wpdb;	$query = "SELECT "	."`Cards`.`id` AS `id`, "	."`Cards`.`title`, "	."`subtitle`, `serial`, `popular_name`, `unittype`, `grade`, `power`, `shield`, `critical`, `trigger`, `skill`, `effect`, `flavor_text`, `illustrator`, `remark`, "	."`Races`.`title` AS `race`, "	."`Rarities`.`title` AS `rarity`, "	."`Expansions`.`title` AS `expansion`, "	."`Clans`.`title` AS `clan`, "	."`Nations`.`title` AS `nation` "	." FROM `" .$wpdb->prefix . "plugin_vgcardutils_cards` AS `Cards`"	."LEFT JOIN ".$wpdb->prefix . "plugin_vgcardutils_races"." AS `Races` ON (`race_id` = `Races`.`id`) "	."LEFT JOIN ".$wpdb->prefix . "plugin_vgcardutils_rarities"." AS `Rarities` ON (`rarity_id` = `Rarities`.`id`) "	."LEFT JOIN ".$wpdb->prefix . "plugin_vgcardutils_expansions"." AS `Expansions` ON (`expansion_id` = `Expansions`.`id`) "	."LEFT JOIN ".$wpdb->prefix . "plugin_vgcardutils_clans"." AS `Clans` ON (`clan_id` = `Clans`.`id`) "	."LEFT JOIN ".$wpdb->prefix . "plugin_vgcardutils_nations"." AS `Nations` ON (`Clans`.`nation_id` = `Nations`.`id`) "	."WHERE `Cards`.`id` = %d "	.'LIMIT 1';	$result = $wpdb->get_row($wpdb->prepare($query,array('%d'=>$id)));	?><div class="wrap">	<div id="icon-plugins" class="icon32">		<br>	</div>	<h2>查看</h2>	<div>		<div style="float:left;">			<img class="VGCardIMG" src="<?php echo plugin_dir_url(__FILE__)."img/cards/default-image-350x510.jpg"?>"				alt="" />		</div>		<div class="VGFields">				<p>					<p class="VGFieldName">序列号</p>					<p class="VGFieldValue" ><?php echo $result->serial ?></p>				</p>				<p>					<p class="VGFieldName">卡名</p>					<p class="VGFieldValue" ><?php echo $result->title; ?></p>				</p>				<p>					<p class="VGFieldName">副卡名</p>					<p class="VGFieldValue" ><?php echo $result->subtitle; ?></p>				</p>				<p>					<p class="VGFieldName">俗称</p>					<p class="VGFieldValue" ><?php echo $result->popular_name; ?></p>				</p>				<p>					<p class="VGFieldName">单位类型</p>					<p class="VGFieldValue" ><?php global $__VGUnittypeEnum__; echo $__VGUnittypeEnum__[$result->unittype]; ?></p>				</p>				<p>					<p class="VGFieldName">卡包</p>					<p class="VGFieldValue" ><?php echo $result->expansion; ?>					</p>				</p>				<p>					<p class="VGFieldName">等级</p>					<p class="VGFieldValue" ><?php global $__VGGradeEnum__; echo $__VGGradeEnum__[$result->grade]; ?></p>				</p>				<p>					<p class="VGFieldName">力量</p>					<p class="VGFieldValue" ><?php global $__VGPowerEnum__; echo $__VGPowerEnum__[$result->power]; ?></p>				</p>				<p>					<p class="VGFieldName">盾护</p>					<p class="VGFieldValue" ><?php global $__VGShieldEnum__; echo $__VGShieldEnum__[$result->shield]; ?></p>				</p>				<p>					<p class="VGFieldName">☆</p>					<p class="VGFieldValue" ><?php global $__VGCriticalEnum__; echo $__VGCriticalEnum__[$result->critical]; ?></p>				</p>				<p>					<p class="VGFieldName">触发</p>					<p class="VGFieldValue" ><?php global $__VGTriggerEnum__; echo $__VGTriggerEnum__[$result->trigger]; ?></p>				</p>				<p>					<p class="VGFieldName">国家</p>					<p class="VGFieldValue" ><?php echo $result->nation; ?></p>				</p>				<p>					<p class="VGFieldName">集团</p>					<p class="VGFieldValue" ><?php echo $result->clan; ?></p>				</p>				<p>					<p class="VGFieldName">罕见度</p>					<p class="VGFieldValue" ><?php echo $result->rarity; ?></p>				</p>				<p>					<p class="VGFieldName">种族</p>					<p class="VGFieldValue" ><?php echo $result->race; ?></p>				</p>				<p>					<p class="VGFieldName">技能</p>					<p class="VGFieldValue" ><?php global $__VGSkillEnum__; echo $__VGSkillEnum__[$result->skill]; ?></p>				</p>				<p>					<p class="VGFieldName">特效</p>					<p class="VGFieldValue" ><?php echo $result->effect; ?></p>				</p>				<p>					<p class="VGFieldName">台词</p>					<p class="VGFieldValue" ><?php echo $result->flavor_text; ?></p>				</p>				<p>					<p class="VGFieldName">画师</p>					<p class="VGFieldValue" ><?php echo $result->illustrator; ?></p>				</p>				<p>					<p class="VGFieldName">备注</p>					<p class="VGFieldValue" ><?php echo $result->remark; ?></p>				</p>		</div>	</div></div><?php }?>