<?phpinclude plugin_dir_path(__FILE__).'../includes/class-vgexpansions-list-table.php';function vg_expansions_list_page() {	// 	if ( isset($_REQUEST['exid']) ) {	// 		vg_expansion_view_page($_REQUEST['exid']);	// 		return;	// 	}$table = new VgExpansions_List_Table();$table->prepare_items();?><div class="wrap">	<div id="icon-users" class="icon32">		<br />	</div>	<h2>所有卡包</h2>	<?php $table->description()?>	<form id="movies-filter" method="get">		<input type="hidden" name="page"			value="<?php echo $_REQUEST['expage'] ?>" />		<?php $table->display() ?>	</form></div><?php }?><?php function vg_expansion_view_page($id) {	global $wpdb;	$query = "SELECT "	."`Expansions`.`id` AS `id`, "	."`Expansions`.`title` AS `title`, "	." FROM `" .$wpdb->prefix . "plugin_vgcardutils_expansions` AS `Expansions`"	."WHERE `Cards`.`id` = %d "	.'LIMIT 1';	$result = $wpdb->get_row($wpdb->prepare($query,array('%d'=>$id)));	?><div class="wrap">	<div id="icon-plugins" class="icon32">		<br>	</div>	<h2>查看</h2>	<table class="form-table">		<tr valign="top">			<th scope="row">序列号</th>			<td><?php echo $result->id ?></td>		</tr>		<tr valign="top">			<th scope="row">卡名</th>			<td><?php echo $result->title; ?></td>		</tr>	</table></div><?php }?>